import { Component, ElementRef, Injector, OnInit, Renderer2 } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DateAdapter } from '@angular/material/core';
import { CompactType, DisplayGrid, GridType, GridsterConfig, GridsterItem, GridsterItemComponent } from 'angular-gridster2';
import * as moment from 'moment';
import { ToastrService } from 'ngx-toastr';
import { PeopleOptions } from 'src/app/models/enum/People';
import { ReservationData } from 'src/app/models/modelRequest/ReservationData';

@Component({
  selector: 'app-reservation',
  templateUrl: './reservation.component.html',
  styleUrls: ['./reservation.component.css']
})
export class ReservationComponent implements OnInit {
	options !: GridsterConfig;
  dashboard!: GridsterItem[];
  itemToPush!: GridsterItemComponent;
  form!: FormGroup;
  minValue: { hour: number, minute: number } = { hour: 0, minute: 0 }; // Example: Set the minimum time to 00:00
  maxValue: { hour: number, minute: number } = { hour: 24, minute: 0 }; // Example: Set the minimum time to 00:00
  selectedTime: string = '';
  selected !: string;
  selectedPeople: PeopleOptions | null = PeopleOptions.None;

  peopleOptions = PeopleOptions;
  selectedRestaurant !: string;
  restaurantList = [];
  timeList : string[] = [];

  minDate: Date; 
  selectedDate!: Date;

  constructor(
		injector: Injector,
		private toasterService: ToastrService,
		private elementRef: ElementRef,
		private renderer: Renderer2,
    private fb: FormBuilder,
    private dateAdapter: DateAdapter<Date>
	) {     
    this.minDate = new Date();
	}

  ngOnInit(): void {
    this.getOptions();
    this.dashboard = [
      { cols: 2, rows: 1, y: 0, x: 0 },
      { cols: 2, rows: 2, y: 0, x: 2 },
      { cols: 1, rows: 1, y: 0, x: 4 },
      { cols: 3, rows: 2, y: 1, x: 4 },
      { cols: 1, rows: 1, y: 4, x: 5 },
      { cols: 1, rows: 1, y: 2, x: 1 },
      { cols: 2, rows: 2, y: 5, x: 5 },
      { cols: 2, rows: 2, y: 3, x: 2 },
      { cols: 2, rows: 1, y: 2, x: 2 },
      { cols: 1, rows: 1, y: 3, x: 4 },
      { cols: 1, rows: 1, y: 0, x: 6 }
    ];

    this.generateTimeList();
  }

  getOptions() {
    this.options = {
      gridType: GridType.Fixed,
      compactType: CompactType.None,
      emptyCellClickCallback: this.emptyCellClick.bind(this),
      enableEmptyCellClick: true,
      pushItems: true,
      draggable: {
        enabled: true
      },
      resizable: {
        enabled: true
      },
      displayGrid: DisplayGrid.Always
    };
  }

  initItem(item: GridsterItem, itemComponent: GridsterItemComponent): void {
    this.itemToPush = itemComponent;
  }

  removeItem($event: MouseEvent | TouchEvent, item : any): void {
    $event.preventDefault();
    $event.stopPropagation();
    this.dashboard.splice(this.dashboard.indexOf(item), 1);
  }

  addItem(): void {
    this.dashboard.push({ x: 0, y: 0, cols: 1, rows: 1 });
  }
  emptyCellClick(event: MouseEvent, item: GridsterItem): void {
    console.info('empty cell click', event, item);
    this.dashboard.push(item);
  }

  handleTimeSelection(): void {
    console.log('Selected time:', this.selectedTime);
  }

  generateTimeList(): void {
    const currentTime = new Date();
    const nextMultipleOf15 = Math.ceil(currentTime.getMinutes() / 15) * 15;
    currentTime.setMinutes(nextMultipleOf15, 0, 0); // Set to the next multiple of 15 minutes

    const endTime = new Date();
    endTime.setHours(23, 59, 59); // Set the end time to the end of the day

    while (currentTime < endTime) {
      this.timeList.push(this.formatTime(currentTime));
      currentTime.setMinutes(currentTime.getMinutes() + 15);
    }
  }


  formatTime(date: Date): string {
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    return `${hours}:${minutes}`;
  }

  getNumberPeopleType(people: PeopleOptions | null) {
    if (!people) {
      return 0; 
    }

    if (people === PeopleOptions.LessThan2) return 2;
    else if (people === PeopleOptions.MoreThan5) return 8;
    else if (people === PeopleOptions.ThreeToFour) return 4;
    else return 0;
  }

  getRestaurantMap(): void {
    let numberPeople = this.getNumberPeopleType(this.selectedPeople);
    const formattedDate = this.selectedDate?.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
    const formData: ReservationData = {
      restaurantName: this.selectedRestaurant,
      numberPeople: numberPeople,
      reservationDate: formattedDate,
      reservationTime: this.selectedTime
    };

    // Send formData to your service or perform desired actions
    console.log(formData);
  }
}
